style.content:
/* General styles */
body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    line-height: 1.6;
    color: #333;
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
    background-color: #f4f4f4;
}

/* Header styles */
header {
    text-align: center;
    margin-bottom: 40px;
}

h1 {
    color: #2c3e50;
    font-size: 2.5em;
    margin-bottom: 10px;
}

#photo-container {
    position: relative;
    width: 200px;
    height: 200px;
    margin: 20px auto 30px;
}

#photo {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    border: 5px solid #3498db;
    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
}

/* Bio styles */
.bio-container {
    background-color: white;
    border-radius: 10px;
    padding: 20px;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
    max-width: 600px;
    margin: 0 auto;
}

.bio-items {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 15px;
}

.bio-item {
    background-color: #e8f4fc;
    color: #3498db;
    padding: 5px 10px;
    border-radius: 20px;
    font-size: 0.9em;
    display: flex;
    align-items: center;
}

.bio-item i {
    margin-right: 5px;
}

/* Section headers */
h2 {
    color: #2c3e50;
    text-align: center;
    font-size: 2em;
    margin-top: 40px;
}

/* Projects styles */
.projects {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 20px;
    padding: 20px 0;
}

.project-card {
    background-color: white;
    border-radius: 10px;
    padding: 20px;
    box-shadow: 0 4px 6px rgba(0,0,0,0.1);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.project-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 6px 12px rgba(0,0,0,0.15);
}

.project-logo {
    width: 100px;
    height: 100px;
    object-fit: contain;
    margin-bottom: 15px;
}

.project-title {
    font-size: 1.2em;
    margin-bottom: 10px;
    color: #3498db;
}

.project-description {
    font-size: 0.9em;
    color: #666;
    margin-bottom: 15px;
}

.project-links {
    display: flex;
    flex-wrap: wrap;
    gap: 10px;
}

.project-link {
    font-size: 0.9em;
    color: #3498db;
    text-decoration: none;
    transition: color 0.3s ease;
    display: flex;
    align-items: center;
}

.project-link:hover {
    color: #2980b9;
}

.project-link i {
    margin-right: 5px;
}

/* Talks styles */
.talks {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
    gap: 20px;
}

.talk-card {
    width: 300px;
    background-color: #f8f9fa;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease;
}

.talk-card:hover {
    transform: translateY(-5px);
}

.talk-thumbnail {
    width: 100%;
    height: 169px; /* 16:9 aspect ratio */
    object-fit: cover;
}

.talk-title {
    padding: 10px;
    font-size: 1.1em;
    color: #333;
    text-align: center;
}

.talk-card a {
    text-decoration: none;
}

/* Footer styles */
footer {
    margin-top: 40px;
    padding-top: 20px;
    border-top: 1px solid #e0e0e0;
}

.social-links {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    padding: 0;
    list-style-type: none;
}

.social-links li {
    margin: 0 15px 15px;
}

.social-links a {
    display: flex;
    flex-direction: column;
    align-items: center;
    color: #3498db;
    transition: color 0.3s ease, transform 0.3s ease;
    text-decoration: none;
}

.social-links a:hover {
    color: #2980b9;
    transform: translateY(-3px);
}

.social-links i {
    font-size: 24px;
    margin-bottom: 5px;
}

.social-links span {
    font-size: 12px;
}

/* Media queries */
@media (max-width: 600px) {
    body {
        padding: 10px;
    }
    #photo-container {
        width: 150px;
        height: 150px;
    }
    .bio-items {
        gap: 10px;
    }
    .bio-item {
        font-size: 0.8em;
    }
    .social-links li {
        margin: 0 10px 15px;
    }
}
